import Sidebar from './Sidebar'
import Widgets from './Widgets'
impo

function App() {
  const [{user}, dispatch]=userStateValue();
  return (
    <div className="app">
    {!user?( 
    <Login />):
    (
      <>
          <Header/>
        <div className="app_body">
          <Sidebar/>
          <Feed/>
          <Widgets/>
        </div>
      </>
    )}
    </div>
  );
}

export default App;
