import { Avatar, Button } from '@material-ui/core'
import React, { useState } from 'react'
import './MessageSender.css'
import VideocamIcon from '@material-ui/icons/Videocam';
import PhotoLibraryIcon from '@material-ui/icons/PhotoLibrary';
import EmojiEmotionsIcon from '@material-ui/icons/EmojiEmotions';

function MessageSender() {

    const[ input ,setinput]=useState[""]

    const handleSubmit = e => {
        e.preventDefault();
    };


    return (
        <div className="MessageSender">
        <div className= "MessageSender_top">
            <Avatar/>
            <form>
                <input 
                value={input}
                onChange={(e)=>set}
                className="MessageSender_input" placeholder="What's on your mind?" />
                <input  placeholder="Image URL (Optional)"/>
                <Button onClick={handleSubmit} type="Submit">
                    Hidden Submit
                </Button>
            </form>
        </div>
        <div className="MessageSender_bottom">
            <div className="MessageSender_option">
                <VideocamIcon style={{color: "red"}}/><h3>Live Video</h3>
                <PhotoLibraryIcon style={{color: "green"}}/><h3>Photos/videos</h3>
                <EmojiEmotionsIcon  style={{color: "Yellow"}}/><h3>Feelings</h3>
            </div>

        </div> 
            
        </div>
    )
}

export default MessageSender
